create database QUIZDB;use QUIZDB;create Table IF NOT EXISTS TOPICS (TOPICID MEDIUMINT AUTO_INCREMENT,NAME       VARCHAR(30)  NOT NULL,PRIMARY KEY (TOPICID)) ENGINE=InnoDB;  COMMIT;
use QUIZDB;create Table IF NOT EXISTS USERS (USERID   VARBINARY(40)  UNIQUE NOT NULL,PASSWORD VARBINARY(40)  NOT NULL,HINT     VARBINARY(80)  NOT NULL,EMAIL    VARCHAR(80)  NULL,PRIMARY KEY (USERID)) ENGINE=InnoDB;  COMMIT;
use QUIZDB;create Table IF NOT EXISTS GAMES (GAMEID  MEDIUMINT  AUTO_INCREMENT,TOPICID   MEDIUMINT NOT NULL,TOTPLAYERS SMALLINT NOT NULL,NUMPLAYERS SMALLINT DEFAULT 0,PLAYER1   VARBINARY(40)  DEFAULT NULL,PLAYER2   VARBINARY(40)  DEFAULT NULL,PLAYER3   VARBINARY(40)  DEFAULT NULL,PLAYER4   VARBINARY(40)  DEFAULT NULL,PLAYER5   VARBINARY(40)  DEFAULT NULL,KEY GAMEID (GAMEID) ) ENGINE=InnoDB;  COMMIT;
use QUIZDB;ALTER TABLE GAMES ADD FOREIGN KEY (TOPICID) REFERENCES TOPICS (TOPICID)ON UPDATE CASCADE ON DELETE RESTRICT; COMMIT;
use QUIZDB;create Table IF NOT EXISTS QUESTIONS (QUESTIONID     MEDIUMINT  AUTO_INCREMENT,TOPICID   MEDIUMINT DEFAULT NULL,QUESTION  VARBINARY(200)  NOT NULL,OPTION1   VARBINARY(200)  NOT NULL,OPTION2   VARBINARY(200)  NOT NULL,OPTION3   VARBINARY(200)  NOT NULL,OPTION4   VARBINARY(200)  NOT NULL,ANSWERIDX SMALLINT NOT NULL,KEY QUESTIONID (QUESTIONID) ) ENGINE=InnoDB;  COMMIT;
use QUIZDB;ALTER TABLE QUESTIONS ADD FOREIGN KEY (TOPICID) REFERENCES TOPICS (TOPICID)ON UPDATE CASCADE ON DELETE RESTRICT; COMMIT;
